class Pokemon{URL="https://pokeapi.co/api/v2/pokemon/?limit=200";async loadPokemons(){this.showLoadingMessage();let e=await fetch(this.URL),t=await e.json();this.pokemonArray=t.results,this.hideLoadingMessage(),this.processPokemons()}processPokemons(){let e=document.getElementById("pokemonButtons");this.pokemonArray.forEach(t=>e.appendChild(this.createButton(t)))}createButton(e){let t=document.createElement("button");return t.setAttribute("type","button"),t.setAttribute("class","btn btn-primary"),t.setAttribute("data-bs-toggle","modal"),t.setAttribute("data-bs-target","#pokemon-modal"),t.innerText=e.name,t.addEventListener("click",this.modalBodyContent(e)),t}modalBodyContent(e){return()=>{document.getElementById("modalLabel").innerHTML=e.name,document.getElementById("pokemonDetails").innerHTML="",this.getPokemonDetails(e).then(e=>{document.getElementById("pokemonDetails").innerHTML=`<dl class="row"><dt class="col-sm-3">Height</dt><dd class="col-sm-9">${e.height}ft</dd><span></span><dt class="col-sm-3">Weight</dt><dd class="col-sm-9">${e.weight}lbs</dd><span></span><dt class="col-sm-3">Types</dt><dd class="col-sm-9">${e.types}</dd><span></span><dt class="col-sm-3">Abilities</dt><dd class="col-sm-9">${e.abilities}</dd><span></span><dd class="col"><div class="text-center"><img src="${e.image}" /></div></dd></dl>`})}}async getPokemonDetails(e){let t=await fetch(e.url),s=await t.json();return{name:s.name,height:s.height,weight:s.weight,types:s.types.map(e=>e.type.name).join(", "),abilities:s.abilities.map(e=>e.ability.name).join(", "),image:s.sprites.front_default}}showLoadingMessage(){var e=document.createElement("img");e.src="./img/loader.gif",document.getElementById("loader").appendChild(e)}hideLoadingMessage(){document.getElementById("loader").style.display="none"}}new Pokemon().loadPokemons();